(lambda f: [(lambda item, vendors_num, date, budget: ((lambda candidates: (print(f"{item}:"),[print(f"{i+1}: {c[0]} {c[1]:.2f} {c[2]:.1f}") for i, c in enumerate(sorted(candidates, key=lambda c: (-c[2], c[1], c[0])))]))([(name, float(cost), float(rating)) for _ in range(int(vendors_num)) for line in [next(f)] for name, cost, rating, *dates in [line.split()] if date not in dates and float(cost) <= float(budget)])))(*next(f).split()) for _ in range(int(next(f)))] )(open("Input/vendors.dat"))